// ****************************************************************************
// * Tokenizer
// ****************************************************************************
template <int TK>
static int token(YYSTYPE *yylval, int token, const char *tok, int /*updated*/)
{
   assert(TK == token);
   const int sn = yy::Translate(token);
   const char *const name = yy::SymbolName(sn);
   //DBG("\n(TK:%d token:%d sn:%d '%s:%s')", TK, token, sn, tok, name);
   const char *text = token == NL ? "\\\\n" : yytext;
   *yylval = astNewToken<TK>(sn, name, text);
   return token;
}

// *****************************************************************************
template <int TOK>
static inline int block_comment(void)
{
   //loc.Print();
   if (yyecho) { DBG("\"\"\""); }
   char c, prev = 0, prev2 = 0;
   while ((c = yyinput()) != 0)
   {
      if (c == '\n')
      {
         loc.NewLine();
         if (yyecho) { DBG("\n"); }
         continue;
      }
      if (yyecho) { DBG("%c",c); }
      if (c == '\"' && prev == '\"' && prev2 == '\"') { return EMPTY; }
      prev2 = prev;
      prev = c;
   }
   return EMPTY;
}

// *****************************************************************************
template <int TOK>
static inline int line_comment(void)
{
   //loc.Print();
   if (yyecho) { DBG("#"); }
   char c;
   while ((c = yyinput()) != 0)
   {
      if (yyecho) { DBG("%c",c); }
      if (c == '\n') { loc.NewLine(); return NL; }
   }
   return NL;
}

// *****************************************************************************
template <int TOK>
static inline int back_slash(void)
{
   assert(yytext[0] == '\\');
   assert(yytext[1] == '\n');
   loc.NewLine();
   return EMPTY;
}

// *****************************************************************************
static inline int update(void)
{
   for (int i = 0; yytext[i] != '\0'; i++)
   {
      loc.Echo(yytext[i]);
      if (yytext[i] == '\n') { loc.NewLine(); }
      else if (yytext[i] == '\t') { loc.Column(); }
      else { loc.Column(); }
   }
   if (yyecho) { ECHO; }
   return EMPTY;
}

// *****************************************************************************
void yyerror(YYSTYPE*, char const *error)
{
   char filePath[PATH_MAX];
   const int c = loc.column - 1, l = loc.line;
   const char *LINE = loc.LINE.c_str();
   const bool have_path = fcntl(yyin->_file, F_GETPATH, filePath) != -1;
   if (have_path) { printf("\033[32m%s:%d:%d", filePath, l, c); }
   printf("\n\033[33m%s\n\033[31m%*s\n%*s\033[m\n", LINE, c, "^", c, error);
   fflush(0);
   abort();
}

// *****************************************************************************
template<int SN>
Node* astNewToken(const int sn, const char *name, const char* text)
{
   return astAddNode(std::make_shared<Token<SN>>(sn, name, text));
}

// *****************************************************************************
template<int SN>
void Token<SN>::Apply(struct Middlend &ir, bool &dfs, Node**)
{
   if (dfs && yyecho)
   {
      DBG("\033[33m[Apply] #%d %s (%s)", SN, Name().c_str(), Text().c_str());
   }
   ir.middlend<SN>(this);
}

